import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
// import { deleteUser } from "../features/userDetailSlice";
 // import { showUser } from "../features/userDetailSlice";
const User = () => {
  // const dispatch = useDispatch() // const {users} = useSelector((state)=>state.app) // console.log(users); // useEffect(()=>{ // dispatch(showUser()) // },[])

  const [users, setUsers] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get("http://localhost:3000/api/getall");
      setUsers(response.data);
    };
    fetchData();
  }, []);
  const deleteUser = async (userId) => {
    await axios
      .delete(`http://localhost:3000/api/delete/${userId}`)
      .then((respones) => {
        setUsers((prevUser) => prevUser.filter((user) => user._id !== userId));
      })
      .catch((error) => {
        console.log(error);
      });
  };
  return (
    <div className="usertable">

      <Link to={"/add"} className="btn btn-primary addbtn">

        Add User
      </Link>
      <div className="tbl">

        <table className="table">

          <thead>

            <tr>

              <th scope="col">Name</th> <th scope="col">Username</th>
              <th scope="col">Email</th> <th scope="col">Action</th>
            </tr>
          </thead>
          <tbody>

            {users.map((user, index) => {
              return (
                <tr key={user._id}>

                  <th scope="row">{index + 1}</th>
                  <td>
                    {user.fname} {user.lname}
                  </td>
                  <td>{user.email}</td>
                  <td>

                    <Link to={`/edit/` + user._id} className="btn btn-info edt">

                      Edit
                    </Link>
                    <button
                      onClick={() => deleteUser(user._id)}
                      className="btn btn-danger"
                    >

                      Delete
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};
export default User;
